import React, { useState } from "react";
import './styles/ShoppingListPage.css';

const ShoppingListPage = () => {
  const [items, setItems] = useState([]);  // State to store shopping list items
  const [showPopup, setShowPopup] = useState(false);  // Control popup visibility
  const [newItem, setNewItem] = useState({ name: "", quantity: "" });  // State for new item input

  // Function to open the popup
  const openPopup = () => {
    setNewItem({ name: "", quantity: "" });  // Clear inputs
    setShowPopup(true);
  };

  // Function to close the popup
  const closePopup = () => {
    setShowPopup(false);
  };

  // Function to handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewItem((prev) => ({ ...prev, [name]: value }));
  };

  // Function to add a new item
  const addItem = () => {
    if (newItem.name && newItem.quantity) {
      setItems([...items, newItem]);  // Add new item to list
      closePopup();  // Close popup after adding
    } else {
      alert("Please fill in both fields.");
    }
  };

  return (
    <div className="shopping-list-container">
      <h1 className="shopping-list-header">Shopping List</h1>
      <div className="shopping-list-section">
        {items.length > 0 ? (
          <ul className="shopping-items-list">
            {items.map((item, index) => (
              <li key={index} className="shopping-item">
                {item.name} ({item.quantity})
              </li>
            ))}
          </ul>
        ) : (
          <p>Your shopping list items will appear here.</p>
        )}
      </div>
      <button className="add-shopping-item-button" onClick={openPopup}>+ Add Item</button>

      {/* Popup for Adding Item */}
      {showPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <h3>Add Shopping Item</h3>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={newItem.name}
                onChange={handleInputChange}
                placeholder="e.g., Apples"
              />
            </label>
            <label>
              Quantity:
              <input
                type="text"
                name="quantity"
                value={newItem.quantity}
                onChange={handleInputChange}
                placeholder="e.g., 2 kg, 1 pack"
              />
            </label>
            <div className="popup-buttons">
              <button onClick={closePopup}>Cancel</button>
              <button onClick={addItem}>Add Item</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ShoppingListPage;
